mapper.select:
select
    id, name, password_hash AS passwordHash, registration, email, salt
 from users


        and id = #{id}


        and name = #{name}


        and passwordHash = #{passwordHash}


        and email = #{email}


        and salt = #{salt}

mapper.insert:
insert into users(name, password_hash, email, salt)
values (
    #{name}, #{passwordHash}, #{email}, #{salt}
)
